(176, 50, 1, '', '2015-01-16 23:28:14', 911, 2325, '607638748e0f4d5830cee9f525c13e2381faef73', '{"id":50,"asset_id":"123","title":"Amumu","alias":"amumu","introtext":"<p><img src=\\"images\\/LOLpersojanes\\/Amumu.jpg\\" alt=\\"\\" \\/>Amumu es un diminuto cad\\u00e1ver ambulante que vaga por el mundo tratando de descubrir su verdadera identidad. Sali\\u00f3 de una antigua tumba de Shurima, envuelto en mortajas, sin recordar nada de su pasado y consumido por una incontrolable melancol\\u00eda.<\\/p>","fulltext":"","state":1,"catid":"20","created":"2015-01-16 22:57:03","created_by":"911","created_by_alias":"","modified":"2015-01-16 23:28:14","modified_by":"911","checked_out":"911","checked_out_time":"2015-01-16 23:27:38","publish_up":"2015-01-16 22:57:03","publish_down":"0000-00-00 00:00:00","images":"{\\"image_intro\\":\\"\\",\\"float_intro\\":\\"\\",\\"image_intro_alt\\":\\"\\",\\"image_intro_caption\\":\\"\\",\\"image_fulltext\\":\\"\\",\\"float_fulltext\\":\\"\\",\\"image_fulltext_alt\\":\\"\\",\\"image_fulltext_caption\\":\\"\\"}","urls":"{\\"urla\\":\\"http:\\\\\\/\\\\\\/localhost\\\\\\/joomla336\\\\\\/index.php\\\\\\/shopping\\\\\\/product\\\\\\/view\\\\\\/1\\\\\\/77\\",\\"urlatext\\":\\"Amumu Robot Trsite\\",\\"targeta\\":\\"\\",\\"urlb\\":\\"http:\\\\\\/\\\\\\/localhost\\\\\\/joomla336\\\\\\/index.php\\\\\\/shopping\\\\\\/product\\\\\\/view\\\\\\/1\\\\\\/76\\",\\"urlbtext\\":\\"Amumu Fara\\\\u00f3n\\",\\"targetb\\":\\"\\",\\"urlc\\":\\"http:\\\\\\/\\\\\\/localhost\\\\\\/joomla336\\\\\\/index.php\\\\\\/shopping\\\\\\/product\\\\\\/view\\\\\\/1\\\\\\/75\\",\\"urlctext\\":\\"Amumu Caballerito Andante\\",\\"targetc\\":\\"\\"}","attribs":"{\\"show_title\\":\\"\\",\\"link_titles\\":\\"\\",\\"show_tags\\":\\"\\",\\"show_intro\\":\\"\\",\\"info_block_position\\":\\"\\",\\"show_category\\":\\"\\",\\"link_category\\":\\"\\",\\"show_parent_category\\":\\"\\",\\"link_parent_category\\":\\"\\",\\"show_author\\":\\"\\",\\"link_author\\":\\"\\",\\"show_create_date\\":\\"\\"